mapper.select:
SELECT pur.id, pur.paper_group_id, ps.paper_group_name, pur.paper_name, pur.review_status, pur.create_time, u.userName, u.id as userId, a.submitNum, ex.examName as testName FROM ( SELECT user_id, max( create_time ) AS create_time, count( 1 ) AS submitNum FROM paper_upload_record GROUP BY user_id ) a LEFT JOIN paper_upload_record pur ON pur.create_time = a.create_time LEFT JOIN user_info u on u.id = pur.user_id LEFT JOIN paper_setting ps on ps.id = pur.paper_group_id LEFT JOIN ${dataBase}.sys_examination ex ON ex.id = ps.test_id AND pur.user_id = a.user_id WHERE pur.deleted = 0
and pur.paper_name like concat ('%', #{entity.paperName},'%' )
and pur.review_status = #{entity.reviewStatus}
and pur.create_time = #{entity.createTime}
GROUP BY pur.user_id,pur.paper_group_id order by pur.create_time desc
